INSERT INTO llx_c_paiement (id,code,libelle,type,active,accountancy_code,module) VALUES (100,'STRIPE','Stripe',2,1,null,null);
INSERT INTO llx_c_paiement (id,code,libelle,type,active,accountancy_code,module) VALUES (101,'PAYPAL','Paypal',2,1,null,null);
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'thirdparty',                      '(PasswordAssistance)',            NULL,   100, '[__[SELLYOURSAAS_NAME]__] - __(SubjectNewPasswordForYouCustomerDashboard)__', 0, '<body>\n <p>\n__(Hello)__,<br><br>\n__(RequestToResetPasswordReceived)__<br><br>__(YouMustClickToChange)__<br><br><a href="__URL_TO_RESET__">__URL_TO_RESET__</a><br><br>__(ApplicantIpAddress)__: __USER_REMOTE_IP__<br>__(ForgetIfNothing)__<br><br><p>\n__(Sincerely)__,<br><br>\n----------------------------------------- <br>The __[SELLYOURSAAS_NAME]__ Team<br>\nEMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br>\n</body>');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'thirdparty',                      '(ChannelPartnerCreated)',         NULL,   100, '[__[SELLYOURSAAS_NAME]__] - Channel Partner Created',                         0, '<body>\n <p>Dear __[SELLYOURSAAS_NAME]__ partner,<br><br>\nWe are delighted to welcome you as a Channel Partner of __[SELLYOURSAAS_NAME]__.\n          </p>\n          <p>\n             Your account has been setup for you. Features for reselling __[SELLYOURSAAS_NAME]__ are now available in the same dashboard as your customer dashboard, so:\n          </p>\n          <ul>\n              <li>Login link: <a href="__[SELLYOURSAAS_ACCOUNT_URL]__">__[SELLYOURSAAS_ACCOUNT_URL]__</a></li>\n              <li>Username: <strong>__THIRDPARTY_EMAIL__</strong></li>\n          </ul>                \n          <p>\n              Sincerely, <br />\n              The __[SELLYOURSAAS_NAME]__ Team <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'InstanceDeployed',                NULL,   100, 'Welcome to __[SELLYOURSAAS_NAME]__ - Your instance __REFCLIENT__ is ready',   0, '<body>\n <p>Welcome,<br><br>\nWe are delighted to welcome you as a user of __[SELLYOURSAAS_NAME]__, the Ondemand service of your __PACKAGELABEL__.\n                <br>\n                   Your application is installed, setup and ready for you.\n                   Here are the details you need to get started:\n               \n                <br /><br /><strong>Your __PACKAGELABEL__ :</strong>\n                <ul>\n                    <li>URL: <a href="https://__REFCLIENT__?username=__APPUSERNAME__">https://__REFCLIENT__</a></li>\n                    <li>Login: __APPUSERNAME__</li>\n                </ul>\n                <br /><strong>Your customer dashboard :</strong>\n                <ul>\n                    <li>URL: <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">__[SELLYOURSAAS_ACCOUNT_URL]__</a></li>\n                    <li>Login: __THIRDPARTY_EMAIL__</li>\n                </ul>                \n            \n            <br />__EMAIL_FOOTER__</p>\n            Sincerely, <br />\n            The __[SELLYOURSAAS_NAME]__ Team <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n            ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'InstanceUndeployed',              NULL,   100, '[__[SELLYOURSAAS_NAME]__] - Destruction of your instance __REFCLIENT__',      0, '<body>\n <p>Dear __[SELLYOURSAAS_NAME]__ user,<br><br>\nYou made a request from your __[SELLYOURSAAS_NAME]__ customer dashboard to delete your instance <b>__REFCLIENT__</b>.\n                <br>\n                    Your instance was suspended and your data will be destroyed in a few days. For an immediate deletion, please click on this link:<br><a href="__[SELLYOURSAAS_ACCOUNT_URL]__?contractid=__ID__&action=undeployconfirmed&hash=__HASH__">I confirm deletion of __REFCLIENT__</a>               \n            \n            <br /></p>\n            Sincerely, <br />\n            The __[SELLYOURSAAS_NAME]__ Team <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n            ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'GentleTrialExpiringReminder',     NULL,   100, '[__[SELLYOURSAAS_NAME]__] - Your trial of __REFCLIENT__ will soon expire',    0, '<body>\n <p>Dear __[SELLYOURSAAS_NAME]__ user,<br><br>\nThis is a reminder that the trial of your instance <a href="https://__REFCLIENT__">__REFCLIENT__</a> will expire soon (__SELLYOURSAAS_EXPIRY_DATE__). If you wish to continue using this service, please login to your customer console to add a payment method (Credit or Debit card accepted):\n          </p>\n          <p>\nClick here to go on your customer console: <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">__[SELLYOURSAAS_ACCOUNT_URL]__</a><br />\nRemind: Your customer dashboard login is <strong>__THIRDPARTY_EMAIL__</strong><br /><br />\nIf you have entered a payment mode recently, thank you to ignore this email.<br />\n</p>\n          <br />\n            Sincerely, <br />\n            The __[SELLYOURSAAS_NAME]__ Team <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n       ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'EndOfTrial',                      NULL,   100, '[__[SELLYOURSAAS_NAME]__] - Your trial of __REFCLIENT__ has come to an end',  0, '<body>\n <p>Dear __[SELLYOURSAAS_NAME]__ user,<br><br>\nThank you for taking the time to check out  __[SELLYOURSAAS_NAME]__. Your trial account for our service at <strong>__REF_CLIENT__</strong> has now expired. If you wish to continue using this service, please login to your dashboard and add a payment method: <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">__[SELLYOURSAAS_NAME]__ dashboard</a>.<br /><br />\nRemind: Your __[SELLYOURSAAS_NAME]__ dashboard login is <strong>__THIRDPARTY_EMAIL__</strong><br /><br />If you don''t want to continue using this service, no action is needed: Your data will be automatically destroyed in a few days.</p><br />\n Sincerely, <br /> \n               The __[SELLYOURSAAS_NAME]__ Team <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n       ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'facture_send',                    'InvoicePaymentSuccess',           NULL,   100, '[__[SELLYOURSAAS_NAME]__] - Invoice Payment Success __REF__',                 0, '<body>\n <p>Dear __[SELLYOURSAAS_NAME]__ user,<br><br>\nThis email confirms a successful payment (__AMOUNT_FORMATED__) from your __[SELLYOURSAAS_NAME]__ instance <strong>__REFCLIENT__</strong> account. You can login to your <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">__[SELLYOURSAAS_NAME]__ dashboard</a> to download your PDF invoices at any time.<br />\nRemind: Your __[SELLYOURSAAS_NAME]__ dashboard login is <strong>__THIRDPARTY_EMAIL__</strong><br />\n            </p>\n            <p><a href="__DIRECTDOWNLOAD_URL_INVOICE__">Direct link to download the PDF of this invoice</a><br /><br />If you have any questions relating to the above please do not hesitate get in touch.</p>\n<br />\n\n            Sincerely, <br />\n            The __[SELLYOURSAAS_NAME]__ Team <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'facture_send',                    'InvoicePaymentFailure',           NULL,   100, '[__[SELLYOURSAAS_NAME]__] - Invoice Payment Failure __REF__',                 0, '<body>\n <p>Dear __[SELLYOURSAAS_NAME]__ user,<br><br>\nAn attempt to take payment for your __[SELLYOURSAAS_NAME]__ subscription (invoice __REF__ for the instance __REFCLIENT__) has failed. Please update your payment method, or contact your bank or payment method provider.<br />\n                Should failure to take this payment continue, access to our service will be discontinued, and any data you have with us maybe lost.<br />\n<br />\nPlease login to your <a href="__[SELLYOURSAAS_ACCOUNT_URL]__">__[SELLYOURSAAS_NAME]__ dashboard</a> to update and fix your payment information as soon as possible to prevent any interuptions in service.<br />\nRemind: Your __[SELLYOURSAAS_NAME]__ dashboard login is <strong>__THIRDPARTY_EMAIL__</strong><br />\n              </p>\n              <p>\n                The error we received from your bank was: <br />\n                __SELLYOURSAAS_PAYMENT_ERROR_DESC__\n              </p><br />\n\n            Sincerely, <br />\n            The __[SELLYOURSAAS_NAME]__ Team <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n          ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'CustomerAccountSuspendedTrial',   NULL,   100, '[__[SELLYOURSAAS_NAME]__] - Account Suspension of __REFCLIENT__ after end of trial', 0, '<body>\n <p>Dear __[SELLYOURSAAS_NAME]__ user,<br><br>\nWe wish to inform you that your instance __REFCLIENT__ has been suspended.<br>This is likely due to the end of your trial period. If you wish to continue using your application, please login to your <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">__[SELLYOURSAAS_NAME]__ dashboard</a> to enter a payment mode. Your instance access will be restored. If you don''t want to engage with us, no action is needed: Your instance and data will we deleted in a few days.</p><br />\n Sincerely, <br /> \n               The __[SELLYOURSAAS_NAME]__ Team <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n       ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'CustomerAccountSuspended',        NULL,   100, '[__[SELLYOURSAAS_NAME]__] - Account Suspension of __REFCLIENT__',                    0, '<body>\n <p>Dear __[SELLYOURSAAS_NAME]__ user,<br><br>\nWe wish to inform you that your instance __REFCLIENT__ has been suspended.<br>This is likely due to a payment problem.  If you wish to continue using your application, please login to your <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">__[SELLYOURSAAS_NAME]__ dashboard</a> to fix your payment mode. Your instance access will be restored. If you don''t want to continue with us, just ignore this email and following reminders: Your instance and data will we deleted in a few weeks.</p><br />\n<br />\n Sincerely, <br />\n               The __[SELLYOURSAAS_NAME]__ Team <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n       ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'CustomerInstanceUpgraded',        NULL,   100, '[__[SELLYOURSAAS_NAME]__] - Upgrade of instance __REFCLIENT__',                      0, '<body>\n <p>Dear __[SELLYOURSAAS_NAME]__ user,<br><br>\nWe wish to inform you your instance has been upgraded to a higher stable version.<br>If you experience problem after this upgrade, you can contact us at __[SELLYOURSAAS_MAIN_EMAIL]__ </p><br />\n Sincerely, <br /> \n               The __[SELLYOURSAAS_NAME]__ Team <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n       ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'thirdparty',                      'YourYearlyPaymentRequest',        NULL,   100, '[__[SELLYOURSAAS_NAME]__] - Your yearly payment request for your __[SELLYOURSAAS_NAME]__ subscription',      0, '<body>\n <p>Dear __[SELLYOURSAAS_NAME]__ user,<br><br>\nYour request to make a yearly payment for the subscription to __[SELLYOURSAAS_NAME]__ was recorded. Just after next deadline, the payment will be done for the full year.<br>For payment per credit/debit card, you will receive an email to make the payment online.<br /><br />\nThe __[SELLYOURSAAS_NAME]__ Team <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'facture_send',                    'YourPaymentRemind',               NULL,   100, '[__[SELLYOURSAAS_NAME]__] - Reminder for paying your __[SELLYOURSAAS_NAME]__ subscription',                  0, '<body>\n <p>Dear __[SELLYOURSAAS_NAME]__ user,<br><br>\nExcept error on our part, the invoice __REF__ for your subscription to __[SELLYOURSAAS_NAME]__ service seems to be unpaid. This is a remind of the link to download your invoice and to use the pay online your subscription (the covered period appears on the invoice). We recommend you to pay within a delay of 7 days to avoid any suspension of service.<br>__ONLINE_PAYMENT_URL__&suffix=__[SELLYOURSAAS_NAME]__<br><br>\nThe __[SELLYOURSAAS_NAME]__ Team <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'facture_send',                    'YourLinkForYourPayment',          NULL,   100, '[__[SELLYOURSAAS_NAME]__] - Request for the payment of your __[SELLYOURSAAS_NAME]__ subscription',           0, '<body>\n <p>Dear __[SELLYOURSAAS_NAME]__ user,<br><br>\nA new period will start for your subscription to __[SELLYOURSAAS_NAME]__ service. This is the link to download your invoice and to use the pay online your subscription (the covered period appears on the invoice). We recommend you to pay within a delay of 15 days to avoid any suspension of service.<br>__ONLINE_PAYMENT_URL__&suffix=__[SELLYOURSAAS_NAME]__<br><br>\nThe __[SELLYOURSAAS_NAME]__ Team <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'thirdparty',                      '(AlertCreditCardExpiration)',     NULL,   100, '[__[SELLYOURSAAS_NAME]__] - Your credit card is expiring',            0, '<body>\n <p>Dear __[SELLYOURSAAS_NAME]__ user,<br><br>\nWe wish to inform you that your payment method (Credit card ....__CARD_LAST4__) will soon expire.<br />\n              \n              Please login to your <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">__[SELLYOURSAAS_NAME]__ dashboard</a> to update your credit card information as soon as possible to prevent any interuptions in service.<br />\nRemind: Your DoliCloud dashboard login is <strong>__THIRDPARTY_EMAIL__</strong><br />\n            </p>\n            <p>If you have any questions relating to the above please do not hesitate get in touch.</p>\n<br />\n\n            Sincerely, <br />\n            The __[SELLYOURSAAS_NAME]__ Team <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'InstanceDeployed',                'fr_FR',100, 'Bienvenue sur __[SELLYOURSAAS_NAME]__ - Votre instance __REFCLIENT__ est prête',         0, '<body>\n <p>Bienvenue,<br><br>\nNous sommes heureux de vous accueillir comme utilisateur/utilisatrice de __[SELLYOURSAAS_NAME]__, le service à la demande de votre __PACKAGELABEL__.\n                <br>\n                   Votre application est installée, configurée et prête pour vous.\n                   Voici les détails à connaitre pour démarrer:\n               \n                <br /><br /><strong>Votre __PACKAGELABEL__ :</strong>\n                <ul>\n                    <li>URL: <a href="https://__REFCLIENT__?username=__APPUSERNAME__">https://__REFCLIENT__</a></li>\n                    <li>Login: __APPUSERNAME__</li>\n                </ul>\n                <br /><strong>Votre espace client :</strong>\n                <ul>\n                    <li>URL: <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">__[SELLYOURSAAS_ACCOUNT_URL]__</a></li>\n                    <li>Login: __THIRDPARTY_EMAIL__</li>\n                </ul>                \n            \n            <br />__EMAIL_FOOTER__</p>\n            Cordialement, <br />\n            L''équipe __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n            ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'InstanceUndeployed',              'fr_FR',100, '[__[SELLYOURSAAS_NAME]__] - Destruction de votre instance __REFCLIENT__',               0, '<body>\n <p>Cher utilisateur/utilisatrice __[SELLYOURSAAS_NAME]__,<br><br>\nVous avez réalisé une demande depuis votre espace client __[SELLYOURSAAS_NAME]__ afin de détruire votre instance <b>__REFCLIENT__</b>.\n                <br>\n                    Votre instance a déjà été suspendue et vos données seront détruites dans quelques jours. Pour une destruction immédiate, cliquez le lien suivant:<br><a href="__[SELLYOURSAAS_ACCOUNT_URL]__?contractid=__ID__&action=undeployconfirmed&hash=__HASH__">Je confirme la destruction de __REFCLIENT__</a>               \n            \n            <br /></p>\n            Cordialement, <br />\n            L''équipe __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n            ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'GentleTrialExpiringReminder',     'fr_FR',100, '[__[SELLYOURSAAS_NAME]__] - Votre période d''essai pour __REFCLIENT__ expire bientôt',  0, '<body>\n <p>Cher utilisateur/utilisatrice __[SELLYOURSAAS_NAME]__,<br><br>\nCeci est un rappel pour vous informer que votre période de test pour l''instance <a href="https://__REFCLIENT__">__REF_CLIENT__</a> va bientot expirer (le __SELLYOURSAAS_EXPIRY_DATE__). Si vous désirez continuer à utiliser le service, merci de vous logguer à votre espace client pour ajouter un mode de paiement (Carte de crédit ou débit).\n          </p>\n          <p>\nPour cela, cliquer ici pour basculer sur votre espace client: <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">__[SELLYOURSAAS_ACCOUNT_URL]__</a><br />\nRappel: Votre login de l''espace client est <strong>__THIRDPARTY_EMAIL__</strong><br /><br />\nSi vous avez renseigné un mode de paiement récemment, merci d''ignorer cet email.<br />\n</p>\n          <br />\n            Cordialement, <br />\n            L''équipe __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n       ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'EndOfTrial',                      'fr_FR',100, '[__[SELLYOURSAAS_NAME]__] - Votre période d''essai pour __REFCLIENT__ est terminée',    0, '<body>\n <p>Cher utilisateur/utilisatrice __[SELLYOURSAAS_NAME]__,<br><br>\nMerci d''avoir pris le temps de tester __[SELLYOURSAAS_NAME]__. Votre période d''essai pour le service <strong>__REFCLIENT__</strong> est maintenant expirée. Si vous désirez continuer à utiliser ce service, merci de vous connecter à votres espace client et ajouter un mode de paiement: <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">Espace client __[SELLYOURSAAS_NAME]__</a>.<br /><br />\nRappel: Votre login de l''espace client est <strong>__THIRDPARTY_EMAIL__</strong><br /><br />Si vous ne désirez plus utiliser le service, ignorez simplement ce dernier rappel. Vos données seront automatiquement détruites dans quelques jours.</p><br />\n Cordialement, <br /> \n               L''équipe __[SELLYOURSAAS_NAME]__ <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n       ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'facture_send',                    'InvoicePaymentSuccess',           'fr_FR',100, '[__[SELLYOURSAAS_NAME]__] - Paiement réussi de la facture __REF__',                     0, '<body>\n <p>Cher utilisateur/utilisatrice __[SELLYOURSAAS_NAME]__,<br><br>\nCet email confirme un paiement avec succès (__AMOUNT_FORMATED__) pour votre instance __[SELLYOURSAAS_NAME]__ <strong>__REFCLIENT__</strong>. Vous pouvez vous logguer sur votre <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">Espace client __[SELLYOURSAAS_NAME]__</a> pour télécharger vos factures PDF à tout moment.<br />\nRappel: Votre login de votre Espace Client __[SELLYOURSAAS_NAME]__ est <strong>__THIRDPARTY_EMAIL__</strong><br />\n            </p>\n            <p><a href="__DIRECTDOWNLOAD_URL_INVOICE__">Lien direct pour télécharger le PDF de cette facture</a><br /><br />Si vous avez des questions en rapport avec ce message, n''hésitez pas à nous contacter.</p>\n<br />\n\n            Cordialement, <br />\n            L''équipe __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'facture_send',                    'InvoicePaymentFailure',           'fr_FR',100, '[__[SELLYOURSAAS_NAME]__] - Paiement en échec de la facture __REF__',                   0, '<body>\n <p>Cher utilisateur/utilisatrice __[SELLYOURSAAS_NAME]__,<br><br>\nUne tentative pour réaliser le paiement de votre abonnement __[SELLYOURSAAS_NAME]__ (facture __REF__ pour l''instance __REFCLIENT__) a échoué.\n\nMerci de vérifier/mettre à jour votre mode de paiement, ou contacter votre banque ou fournisseur de votre mode de paiement pour autoriser les prochaines tentatives si ce dernier est bien correct.<br />\n                Plusieurs tentatives seront réalisées mais au cas où les échecs de paiements se prolongeraient, l''accès au service pourrait être interrompu (et vos données pourraient être détruites 2 mois après).<br />\n<br />\nMerci de vous logguer à votre <a href="__[SELLYOURSAAS_ACCOUNT_URL]__">Espace Client __[SELLYOURSAAS_NAME]__</a> pour corriger votre mode de paiement si ce dernier le nécessite, pour éviter toute interruption de service.<br />\nRappel: Votre login d''accès à l''Espace Client  __[SELLYOURSAAS_NAME]__ est <strong>__THIRDPARTY_EMAIL__</strong><br />\n              </p>\n              <p>\n                L''erreur renvoyée par votre banque est la suivante: <br />\n                __SELLYOURSAAS_PAYMENT_ERROR_DESC__\n              </p><br />\n\n            Cordialement, <br />\n            L''équipe __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n          ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'CustomerAccountSuspendedTrial',   'fr_FR',100, '[__[SELLYOURSAAS_NAME]__] - Suspension de l''instance __REFCLIENT__ après fin de période d''essai', 0, '<body>\n <p>Cher utilisateur/utilisatrice __[SELLYOURSAAS_NAME]__,<br><br>\nNous attirons votre attention sur le fait que votre instance __REFCLIENT__ a été suspendue.<br>Ceci est probablement due à la fin de votre période d''essai. Si vous désirez continuer à utiliser l''application, merci de vous connecter à votre <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">Espace Client __[SELLYOURSAAS_NAME]__</a> pour entrer un mode de paiement. L''accès à votre instance sera alors rétabli immédiatement. Si vous ne désirez pas continuer, ignorez simplement cet email et votre instance et ses données seront détruites dans quelques jours.</p><br />\n Cordialement, <br />\n            L''équipe __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n       ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'CustomerAccountSuspended',        'fr_FR',100, '[__[SELLYOURSAAS_NAME]__] - Suspension de l''instance __REFCLIENT__',                               0, '<body>\n <p>Cher utilisateur/utilisatrice __[SELLYOURSAAS_NAME]__,<br><br>\nNous attirons votre attention sur le fait que votre instance __REFCLIENT__ a été suspendue.<br>Ceci est probablement due à des problèmes de paiements. Si vous désirez continuer à utiliser l''application, merci de vous connecter à votre <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">Espace Client __[SELLYOURSAAS_NAME]__</a> pour entrer un mode de paiement. L''accès à votre instance sera alors rétabli immédiatement. Si vous ne désirez pas continuer, ignorez simplement cet email et les autres rappels à venir, et votre instance et ses données seront détruites dans quelques semaines.</p><br />\n Cordialement, <br />\n            L''équipe __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n       ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'CustomerInstanceUpgraded',        'fr_FR',100, '[__[SELLYOURSAAS_NAME]__] - Mise à jour de l''instance __REFCLIENT__',                              0, '<body>\n <p>Cher utilisateur/utilisatrice __[SELLYOURSAAS_NAME]__,<br><br>\nNous vous informons que votre instance a été mise à jour sur une version stable supérieure.<br>Si vous rencontrez des difficultés après cette mise à jour, contacter nous à __[SELLYOURSAAS_MAIN_EMAIL]__ </p><br />\n Cordialement, <br /> \n               L''équipe __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n       ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'thirdparty',                      'YourYearlyPaymentRequest',        'fr_FR',100, '[__[SELLYOURSAAS_NAME]__] - Votre demande de paiement annuel pour votre abonnement __[SELLYOURSAAS_NAME]__',  0, '<body>\n <p>Cher utilisateur/utilisatrice __[SELLYOURSAAS_NAME]__,<br><br>\nVotre demande de paiement annuel pour l''abonnement au service __[SELLYOURSAAS_NAME]__ a été enregistrée. A la prochaine échance, le paiement se fera pour une année entière.<br>Dans le cas de paiement par carte, vous recevrez un mail, peu après le début de la nouvelle période annuelle pour réaliser le paiement en ligne.<br /><br />\nL''équipe __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'facture_send',                    'YourPaymentRemind',               'fr_FR',100, '[__[SELLYOURSAAS_NAME]__] - Relance paiement abonnement  __[SELLYOURSAAS_NAME]__',                            0, '<body>\n <p>Cher utilisateur/utilisatrice __[SELLYOURSAAS_NAME]__,<br><br>\nSauf erreur de notre part, la facture __REF__ pour votre abonnement au service __[SELLYOURSAAS_NAME]__ n''a pas encore été payée. Voici un rappel du lien permettant de télécharger la facure et réaliser le paiement en ligne (la période couverte apparait sur la facture). Nous vous recommandons de réaliser le paiement dans un délai de 7 jours afin d''éviter la suspension du service.<br>__ONLINE_PAYMENT_URL__&suffix=__[SELLYOURSAAS_NAME]__<br /><br />\nL''équipe __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'facture_send',                    'YourLinkForYourPayment',          'fr_FR',100, '[__[SELLYOURSAAS_NAME]__] - Requête pour le paiement de votre abonnement __[SELLYOURSAAS_NAME]__',            0, '<body>\n <p>Cher utilisateur/utilisatrice __[SELLYOURSAAS_NAME]__,<br><br>\nUne nouvelle période démarre pour votre abonnement au service __[SELLYOURSAAS_NAME]__. Voici le lien à utiliser pour télécharger votre facture et réaliser le paiement en ligne (la période couverte apparait sur la facture). Nous vous recommandons de payer dans un délai de 15 jours pour éviter toute suspension de service ultérieure.<br>__ONLINE_PAYMENT_URL__&suffix=__[SELLYOURSAAS_NAME]__<br><br>\L''équipe __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'thirdparty',                      '(AlertCreditCardExpiration)',     'fr_FR',100, '[__[SELLYOURSAAS_NAME]__] - Expiration de votre carte bancaire',            0, '<body>\n <p>Cher utilisateur/utilisatrice __[SELLYOURSAAS_NAME]__,<br><br>\nNous voulions vous informer que votre mode de paiement (Carte bancaire ....__CARD_LAST4__) va bientôt expirer.<br />\n              \n              Merci de vous logguez sur votre <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">espace client __[SELLYOURSAAS_NAME]__</a> pour mettre à jour vos informations de paiement, dès que possible, afin de prévenir toute interruption de service.<br />\nRappel: Le login de votre espace client est <strong>__THIRDPARTY_EMAIL__</strong><br />\n            </p>\n            <p>Si vous avez des questions sur ce point, ne pas hésitez à nous contacter.</p>\n<br />\n<br />\n           Cordialement, <br />\n           L''équipe __[SELLYOURSAAS_NAME]__ <br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'InstanceDeployed',                'es_ES',100, 'Bienvenido a __[SELLYOURSAAS_NAME]__ - Su instancia __REFCLIENT__ está lista',       0, '<body>\n <p>Bienvenida,<br><br>\nEstamos muy contentos de ternerle como usuario de __[SELLYOURSAAS_NAME]__, el servicio en línea de sus aplicaciones __PACKAGELABEL__.\n                <br>\n                   Su aplicación ya se encuentra instalada y disponible para su uso.\n                   Aquí están los detalles que necesita para acceder.:\n               \n                <br /><br /><strong>Su aplicación __PACKAGELABEL__ :</strong>\n                <ul>\n                    <li>URL: <a href="https://__REFCLIENT__?username=__APPUSERNAME__">https://__REFCLIENT__</a></li>\n                    <li>Login: __APPUSERNAME__</li>\n                </ul>\n                <br /><strong>Su área de cliente :</strong>\n                <ul>\n                    <li>URL: <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">__[SELLYOURSAAS_ACCOUNT_URL]__</a></li>\n                    <li>Login: __THIRDPARTY_EMAIL__</li>\n                </ul>                \n            \n            <br />__EMAIL_FOOTER__</p>\n            El equipo __[SELLYOURSAAS_NAME]__ <br />\n            ----------------------------------------- <br />\n            E-Mail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n            ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'InstanceUndeployed',              'es_ES',100, '[__[SELLYOURSAAS_NAME]__] - Destrucción de su instancia __REFCLIENT__',              0, '<body>\n <p>Estimado usuario de __[SELLYOURSAAS_NAME]__,<br><br>\nHiciste una solicitud desde tu panel de control del cliente __[SELLYOURSAAS_NAME]__ para eliminar tu instancia <b>__REFCLIENT__</b>.\n                <br>\n                    Su instancia fue suspendida y sus datos serán destruidos en pocos días. Para una eliminación inmediata, haga clic en este enlace: <br><a href="__[SELLYOURSAAS_ACCOUNT_URL]__?contractid=__ID__&action=undeployconfirmed&hash=__HASH__">Confirmo la eliminación de __REFCLIENT__</a>              \n            \n            <br /></p>\n                        El equipo __[SELLYOURSAAS_NAME]__ <br />\n            ----------------------------------------- <br />\n            E-Mail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n            ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'GentleTrialExpiringReminder',     'es_ES',100, '[__[SELLYOURSAAS_NAME]__] - Su periodo de prueba para __REFCLIENT__ expira pronto',  0, '<body>\n <p>Estimado usuario de __[SELLYOURSAAS_NAME]__,<br><br>\nSu periodo de prueba de su instancia <a href="https://__REFCLIENT__">__REFCLIENT__</a> termina pronto (el __SELLYOURSAAS_EXPIRY_DATE__). Si desea continuar usando el servicio, le agradeceríamos se conectara a su área de cliente para añadir un modo de pago:\n          </p>\n          <p>\nHaga clic aquí para ir a la consola de su cliente: <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">__[SELLYOURSAAS_ACCOUNT_URL]__</a><br />\nRecuerde: Su login de acceso a su área de cliente __[SELLYOURSAAS_NAME]__ es <strong>__THIRDPARTY_EMAIL__</strong><br /><br />\nSi ha ingresado recientemente en un modo de pago, gracias por ignorar este e-mail.<br />\n</p>\n          <br />\n                        El equipo __[SELLYOURSAAS_NAME]__ <br />\n            ----------------------------------------- <br />\n            E-Mail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n       ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'EndOfTrial',                      'es_ES',100, '[__[SELLYOURSAAS_NAME]__] - Su periodo de prueba para __REFCLIENT__ ha expirado',    0, '<body>\n <p>Estimado usuario de __[SELLYOURSAAS_NAME]__,<br><br>\nLe damos las gracias por tomarse el tiempo de probar __[SELLYOURSAAS_NAME]__. Su periodo de prueba de <strong>__REF_CLIENT__</strong> ha expirado. Si desea continuar usando nuestro servicio, le agradeceríamos se conectara a su área de cliente y añada un modo de pago: <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">__[SELLYOURSAAS_NAME]__ dashboard</a>.<br /><br />\nRecuerde: Su login de acceso a su área de cliente __[SELLYOURSAAS_NAME]__ es <strong>__THIRDPARTY_EMAIL__</strong><br /><br />Su instancia se conservará tanto tiempo como desee. Por el contrario, si no desea continuar usando el servicio, simplemente ignore esta alerta. Sus datos y su cuenta serán eliminados en pocos dias.</p><br /> \n               El equipo __[SELLYOURSAAS_NAME]__ <br />\n            ----------------------------------------- <br />\n            E-Mail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n       ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'facture_send',                    'InvoicePaymentSuccess',           'es_ES',100, '[__[SELLYOURSAAS_NAME]__] - Pago de la factura __REF__ realizado',                   0, '<body>\n <p>Estimado usuario de __[SELLYOURSAAS_NAME]__,<br><br>\nEste correo electrónico confirma el pago exitoso (__AMOUNT_FORMATED__) de su cuenta __[SELLYOURSAAS_NAME]__ <strong>__REFCLIENT__</strong>. Puede iniciar sesión en su <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">area de cliente __[SELLYOURSAAS_NAME]__</a> para descargar sus facturas en PDF en cualquier momento.<br />\nRecuerde: Su login de acceso a su área de cliente __[SELLYOURSAAS_NAME]__ es <strong>__THIRDPARTY_EMAIL__</strong><br />\n            </p>\n            <p><a href="__DIRECTDOWNLOAD_URL_INVOICE__">Enlace directo a su factura en PDF</a><br /><br />Si tiene alguna pregunta relacionada con lo anterior, no dude en ponerse en contacto.</p>\n<br />\n\n               El equipo __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            E-Mail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'facture_send',                    'InvoicePaymentFailure',           'es_ES',100, '[__[SELLYOURSAAS_NAME]__] - Error en el pago de la factura __REF__',                 0, '<body>\n <p>Estimado usuario de __[SELLYOURSAAS_NAME]__,<br><br>\nSe produjo un error al intentar realizar el pago de su suscripción a __[SELLYOURSAAS_NAME]__ (factura __REF__ para la aplicación __REFCLIENT__). Actualice su método de pago o comuníquese con su banco o proveedor de métodos de pago. <br />\n Si no continúa este pago, se suspenderá el acceso a nuestro servicio y es posible que pierda los datos que tenga con nosotros. <br />\n<br />\nInicie sesión en su <a href="__[SELLYOURSAAS_ACCOUNT_URL]__"> area de client __[SELLYOURSAAS_NAME]__</a> para actualizar y corregir su tarjeta de crédito lo antes posible para evitar cualquier interupción en el servicio.<br />\nRecuerde: Su login de acceso a su área de cliente __[SELLYOURSAAS_NAME]__ es <strong>__THIRDPARTY_EMAIL__</strong><br />\n              </p>\n              <p>\n                El error que recibimos de su banco fue: <br />\n                __SELLYOURSAAS_PAYMENT_ERROR_DESC__\n              </p><br />\n\n                        El equipo __[SELLYOURSAAS_NAME]__ <br />\n            ----------------------------------------- <br />\n            E-Mail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n          ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'CustomerAccountSuspendedTrial',   'es_ES',100, '[__[SELLYOURSAAS_NAME]__] - Suspensión de la instancia __REFCLIENT__ una vez finalizado el período de prueba', 0, '<body>\n <p>Estimado usuario de __[SELLYOURSAAS_NAME]__,<br><br>\nQueremos informarle que su instancia __REFCLIENT__ ha sido suspendida.<br>Es probable que esto se deba al final de su período de prueba. Si desea continuar usando su aplicación, inicie sesión en su <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">area de cliente __[SELLYOURSAAS_NAME]__</a> para ingresar a un modo de pago. El acceso a su instancia será restaurado. Si no desea participar con nosotros, simplemente ignore este correo electrónico y su instancia se eliminará en pocos días.</p><br />\n                           El equipo __[SELLYOURSAAS_NAME]__ <br />\n            ----------------------------------------- <br />\n            E-Mail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n       ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'CustomerAccountSuspended',        'es_ES',100, '[__[SELLYOURSAAS_NAME]__] - Suspensión de la instancia __REFCLIENT__',                                         0, '<body>\n <p>Estimado usuario de __[SELLYOURSAAS_NAME]__,<br><br>\nQueremos informarle que su instancia __REFCLIENT__ ha sido suspendida.<br>Esto es probablemente debido a un problema de pago. Si desea continuar usando su aplicación, inicie sesión en su <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">area de cliente __[SELLYOURSAAS_NAME]__</a> para corregir su modo de pago. El acceso a su instancia será restaurado. Si no desea continuar con nosotros, simplemente ignore este correo electrónico y siga los recordatorios, y su instancia y sus datos serán eliminados en unas pocas semanas.</p><br />\n<br />\n                           El equipo __[SELLYOURSAAS_NAME]__ <br />\n            ----------------------------------------- <br />\n            E-Mail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n       ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'contract',                        'CustomerInstanceUpgraded',        'es_ES',100, '[__[SELLYOURSAAS_NAME]__] - Actualización de la instancia __REFCLIENT__',                                      0, '<body>\n <p>Estimado usuario de __[SELLYOURSAAS_NAME]__,<br><br>\nDeseamos informarle que su instancia se ha actualizado a una versión estable superior.<br>Si tiene algún problema después de esta actualización, puede contactarnos a __[SELLYOURSAAS_MAIN_EMAIL]__ </p><br /> \n                           El equipo __[SELLYOURSAAS_NAME]__ <br />\n            ----------------------------------------- <br />\n            E-Mail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n       ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'thirdparty',                      'YourYearlyPaymentRequest',        'es_ES',100, '[__[SELLYOURSAAS_NAME]__] - Su solicitud de pago anual para su suscripción __[SELLYOURSAAS_NAME]__',   0, '<body>\n <p>Estimado usuario de __[SELLYOURSAAS_NAME]__,<br><br>\nSu solicitud de pago anual para su suscripción al servicio __[SELLYOURSAAS_NAME]__ ha sido registrado. En el próximo vencimiento, el pago será por un año completo..<br>En el caso del pago con tarjeta, recibirá un e-mail, poco después del comienzo del nuevo período anual para realizar el pago en línea.<br /><br />\nEl equipo __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'facture_send',                    'YourPaymentRemind',               'es_ES',100, '[__[SELLYOURSAAS_NAME]__] - Pago de renovación de su suscripción a  __[SELLYOURSAAS_NAME]__',          0, '<body>\n <p>Estimado usuario de __[SELLYOURSAAS_NAME]__,<br><br>\nSalvo error por nuestra parte, la factura __REF__ de su suscripción al servicio __[SELLYOURSAAS_NAME]__ aún no ha sido pagada. Aquí hay un recordatorio del enlace para descargar la facutura y hacer el pago en línea (el período cubierto aparece en la factura). Le recomendamos que realice el pago dentro de los 7 días para evitar la suspensión del servicio.<br>__ONLINE_PAYMENT_URL__&suffix=__[SELLYOURSAAS_NAME]__<br /><br />\nEl equipo __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'facture_send',                    'YourLinkForYourPayment',          'es_ES',100, '[__[SELLYOURSAAS_NAME]__] - Solicitud de pago de su suscripción __[SELLYOURSAAS_NAME]__',              0, '<body>\n <p>Estimado usuario de __[SELLYOURSAAS_NAME]__,<br><br>\nComienza un nuevo período para su suscripción al servicio __[SELLYOURSAAS_NAME]__. Aquí está el enlace para descargar su factura y realizar el pago en línea (el período cubierto aparece en la factura). Le recomendamos que pague dentro de los 15 días para evitar la suspensión del servicio.<br>__ONLINE_PAYMENT_URL__&suffix=__[SELLYOURSAAS_NAME]__<br><br>\nEl equipo __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
INSERT INTO llx_c_email_templates (module,type_template,label,lang,position,topic,joinfiles,content) VALUES ('sellyoursaas', 'thirdparty',                      '(AlertCreditCardExpiration)',     'es_ES',100, '[__[SELLYOURSAAS_NAME]__] - Caducidad de su tarjeta bancaria',                       0, '<body>\n <p>Estimado usuario de __[SELLYOURSAAS_NAME]__,<br><br>\nQueríamos informarle que su método de pago (tarjeta bancaria ....__CARD_LAST4__) caducará pronto.<br />\n              \nInicie sesión en su <a href="__[SELLYOURSAAS_ACCOUNT_URL]__?username=__THIRDPARTY_EMAIL__">área de cliente __[SELLYOURSAAS_NAME]__</a> para actualizar su información de pago lo antes posible a fin de evitar cualquier interrupción del servicio.<br />\nRappel: Le login de votre espace client est <strong>__THIRDPARTY_EMAIL__</strong>Recordatorio: el inicio de sesión de su área de cliente es <strong>__THIRDPARTY_EMAIL__</strong><br />\n            </p>\n            <p>Si tiene alguna pregunta sobre este punto, no dude en ponerse en contacto con nosotros.</p>\n<br />\n            El equipo __[SELLYOURSAAS_NAME]__<br />\n            ----------------------------------------- <br />\n            EMail: __[SELLYOURSAAS_MAIN_EMAIL]__<br />\n</body>\n        ');
